<template>
  <el-row class="container">
    <el-col :span="24" class="header">
      <el-col :span="10" class="logo" :class="collapsed?'logo-collapse-width':'logo-width'">
        <img src="data:image/svg+xml;base64," alt="logo" class="nav-logo-small">
        <span>{{collapsed?'':sysName}}</span>
      </el-col>
      <el-col :span="10">
        <div class="tools" @click.prevent="collapse">
          <i class="icon iconfont icon-category"></i>
        </div>
      </el-col>
      <el-col :span="4" class="user-info">
        <el-dropdown trigger="hover">
          <span class="el-dropdown-link user-info-inner"><img :src="user.avatar" @error="defaultAvatar"/> {{user.name}}</span>
          <el-dropdown-menu slot="dropdown">
            <el-dropdown-item>我的消息</el-dropdown-item>
            <el-dropdown-item>设置</el-dropdown-item>
            <el-dropdown-item divided @click.native="logout">退出</el-dropdown-item>
          </el-dropdown-menu>
        </el-dropdown>
      </el-col>
    </el-col>
    <el-col :span="24" class="main">
      <aside :class="collapsed?'menu-collapsed':'menu-expanded'">
        <!--导航菜单-->
        <el-menu :default-active="$route.path" unique-opened router :collapse="collapsed">
          <template v-for="(item,index) in $router.options.routes" v-if="!item.hidden">
            <el-submenu v-if="item.children" :index="index+''">
              <template slot="title"><i :class="item.iconCls"></i><span slot="title">{{item.name}}</span></template>
              <template v-for="(subItem,subIndex) in item.children" v-if="!subItem.hidden">
                <el-submenu v-if="subItem.children" :index="index+'-'+subIndex" :key="index+'-'+subIndex">
                  <span slot="title">{{subItem.name}}</span>
                  <el-menu-item v-for="(child,index) in subItem.children" v-if="!child.hidden" :index="child.path" :key="child.path">
                    {{child.name}}
                  </el-menu-item>
                </el-submenu>
                <el-menu-item v-else :index="subItem.path">
                  {{subItem.name}}
                </el-menu-item>
              </template>
            </el-submenu>
            <el-menu-item v-else :index="item.path">
              <i :class="item.iconCls"></i>
              <span slot="title">{{item.name}}</span>
            </el-menu-item>
          </template>
        </el-menu>
      </aside>
      <section class="content-container">
        <div class="grid-content">
          <el-col :span="24" class="breadcrumb-container">
            <strong class="title">{{$route.name}}</strong>
            <el-breadcrumb separator="/" class="breadcrumb-inner">
              <el-breadcrumb-item v-for="item in $route.matched" :key="item.path">
                {{ item.name }}
              </el-breadcrumb-item>
            </el-breadcrumb>
          </el-col>
          <el-col :span="24" class="content-wrapper">
            <transition name="fade" mode="out-in">
              <router-view></router-view>
            </transition>
          </el-col>
        </div>
      </section>
    </el-col>
  </el-row>
</template>

<script>
  import { mapGetters } from 'vuex'

  export default {
    data() {
      return {
        sysName: 'VUE UI',
        collapsed: false
      }
    },
    computed: {
      ...mapGetters({ loading: 'loading', user: 'user' })
    },
    methods: {
      defaultAvatar(e) {
        e.target.src = require('../../static/image/avatar.jpg');
      },
      // 退出登录
      logout() {
        this.$confirm('确认退出吗?', '提示', {
          type: 'warning'
        }).then(() => {
          this.$store.dispatch('logout');
          this.$router.push('/login');
        });
      },
      // 折叠导航栏
      collapse() {
        this.collapsed = !this.collapsed;
      }
    }
  }
</script>

